import React, { useEffect, useState } from 'react';
import { useGame } from '../contexts/GameContext';
import ChatWindow from '../components/game/ChatWindow';
import GameControls from '../components/game/GameControls';
import GameResults from '../components/game/GameResults';
import Spinner from '../components/common/Spinner';

interface GameResult {
  winner?: string;
  forfeitedBy?: string;
  error?: string;
  stats: {
    player1Correct: boolean;
    player2Correct: boolean;
    duration: number;
    messageCount: number;
  };
}

const Game: React.FC = () => {
  const { isPlaying, startGame, endGame, isConnected } = useGame();
  const [gameResult, setGameResult] = useState<GameResult | null>(null);

  useEffect(() => {
    console.log('Connection status:', isConnected);
  }, [isConnected]);

  useEffect(() => {
    console.log('Game status:', isPlaying ? 'playing' : 'not playing');
  }, [isPlaying]);

  // Cleanup only when unmounting, ignore dependency warning
  useEffect(() => {
    return () => {
      if (isPlaying) {
        console.log('Component unmounting, ending game');
        endGame();
      }
    };
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  const handlePlayAgain = () => {
    setGameResult(null);
    startGame();
  };

  if (!isConnected) {
    return (
      <div className="max-w-4xl mx-auto text-center py-12">
        <h2 className="text-2xl font-bold mb-4 text-game-light">Connecting to server...</h2>
        <p className="text-game-light/70 mb-4">Please wait while we establish connection</p>
        <Spinner size="lg" />
      </div>
    );
  }

  if (!isPlaying && !gameResult) {
    return (
      <div className="max-w-4xl mx-auto text-center py-12">
        <h2 className="text-2xl font-bold mb-4 text-game-light">Ready to Play?</h2>
        <button
          onClick={startGame}
          className="btn btn-primary"
        >
          Find Match
        </button>
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto py-8">
      <div className="grid grid-rows-[1fr,auto] gap-4 h-[calc(100vh-12rem)]">
        <ChatWindow />
        <GameControls />
      </div>
      {gameResult && (
        <GameResults 
          result={gameResult} 
          onPlayAgain={handlePlayAgain} 
        />
      )}
    </div>
  );
};

export default Game;
