import React from 'react';
import { useNavigate } from 'react-router-dom';
import { FadeIn } from '../common/Animations';

interface GameResultsProps {
  result: {
    winner?: string;
    score?: number;
    forfeitedBy?: string;
    error?: string;
    stats?: {
      player1Correct: boolean;
      player2Correct: boolean;
      duration: number;
      messageCount: number;
    };
  };
  onPlayAgain: () => void;
}

const GameResults: React.FC<GameResultsProps> = ({ result, onPlayAgain }) => {
  const navigate = useNavigate();

  if (result.error) {
    return (
      <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50">
        <FadeIn>
          <div className="bg-game-dark-800 rounded-lg p-6 max-w-md w-full mx-4">
            <h3 className="text-xl font-bold mb-4 text-game-light">Game Ended</h3>
            <p className="text-game-light-600 mb-6">{result.error}</p>
            <div className="flex justify-end space-x-4">
              <button onClick={() => navigate('/')} className="btn btn-secondary">
                Home
              </button>
              <button onClick={onPlayAgain} className="btn btn-primary">
                Play Again
              </button>
            </div>
          </div>
        </FadeIn>
      </div>
    );
  }

  return (
    <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50">
      <FadeIn>
        <div className="bg-game-dark-800 rounded-lg p-6 max-w-md w-full mx-4">
          <h3 className="text-xl font-bold mb-4 text-game-light">Game Results</h3>

          <div className="space-y-4 mb-6">
            {result.winner && (
              <div className="text-center">
                <div className="text-4xl mb-2">
                  {result.score ? 'ðŸŽ‰' : 'ðŸ˜…'}
                </div>
                <p className="text-xl font-bold text-game-light mb-1">
                  {result.score ? 'Victory!' : 'Good Try!'}
                </p>
                <p className="text-game-light-600">
                  {result.score ? `+${result.score} points` : 'Better luck next time!'}
                </p>
              </div>
            )}

            {result.stats && (
              <>
                <div className="border-t border-game-dark-600 pt-4">
                  <p className="text-sm text-game-light-600">
                    Game Duration: {Math.floor(result.stats.duration / 60)}m {result.stats.duration % 60}s
                  </p>
                  <p className="text-sm text-game-light-600">
                    Messages Exchanged: {result.stats.messageCount}
                  </p>
                </div>
              </>
            )}
          </div>

          <div className="flex justify-end space-x-4">
            <button 
              onClick={() => navigate('/')} 
              className="px-4 py-2 bg-game-dark-600 text-game-light rounded hover:bg-game-dark-500 transition-colors"
            >
              Home
            </button>
            <button 
              onClick={onPlayAgain} 
              className="px-4 py-2 bg-game-primary text-white rounded hover:bg-game-primary-dark transition-colors"
            >
              Play Again
            </button>
          </div>
        </div>
      </FadeIn>
    </div>
  );
};

export default GameResults;
