import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const Home: React.FC = () => {
  const navigate = useNavigate();
  const { user } = useAuth();

  return (
    <div className="max-w-4xl mx-auto text-center">
      <h1 className="text-4xl font-bold mb-6">Welcome to Human or AI?</h1>
      <p className="text-xl mb-8 text-gray-600">
        Test your ability to distinguish between human and AI responses in real-time chat conversations.
      </p>

      <div className="space-y-6">
        <button
          onClick={() => navigate('/game')}
          className="btn btn-primary text-lg px-8 py-3"
        >
          {user ? 'Play Now' : 'Login to Play'}
        </button>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <div className="card">
            <h3 className="text-lg font-semibold mb-2">Chat</h3>
            <p className="text-gray-600">
              Engage in real-time conversations with your opponent
            </p>
          </div>

          <div className="card">
            <h3 className="text-lg font-semibold mb-2">Guess</h3>
            <p className="text-gray-600">
              Try to determine if your chat partner is human or AI
            </p>
          </div>

          <div className="card">
            <h3 className="text-lg font-semibold mb-2">Compete</h3>
            <p className="text-gray-600">
              Climb the leaderboard and improve your detection skills
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
