import React, { useState } from 'react';
import { useGame } from '../../contexts/GameContext';
import { PulseEffect } from '../common/Animations';

interface GameControlsProps {
  onGuessHuman: () => void;
  onGuessAI: () => void;
}

const GameControls: React.FC<GameControlsProps> = ({ onGuessHuman, onGuessAI }) => {
  const [message, setMessage] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const { sendMessage, isPlaying } = useGame();

  const handleSend = () => {
    if (message.trim() && isPlaying) {
      sendMessage(message);
      setMessage('');
      setIsTyping(false);
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
    setMessage(e.target.value);
    if (!isTyping && e.target.value.trim()) {
      setIsTyping(true);
    } else if (isTyping && !e.target.value.trim()) {
      setIsTyping(false);
    }
  };

  return (
    <div className="space-y-4">
      {/* Message Input */}
      <div className="relative">
        <input
          type="text"
          value={message}
          onChange={(e: React.ChangeEvent<HTMLInputElement>) => {
            setMessage(e.target.value);
            if (!isTyping && e.target.value.trim()) {
              setIsTyping(true);
            } else if (isTyping && !e.target.value.trim()) {
              setIsTyping(false);
            }
          }}
          onKeyPress={handleKeyPress}
          className="w-full bg-game-dark-800 text-game-light border border-game-dark-600 rounded-lg px-4 py-3 focus:outline-none focus:border-game-primary"
          placeholder="Type your message..."
        />
        <button
          onClick={handleSend}
          className="absolute right-2 top-1/2 transform -translate-y-1/2 bg-game-primary text-white px-4 py-1.5 rounded-lg hover:bg-game-primary-dark transition-colors"
        >
          Send
        </button>
      </div>

      {/* Guess Buttons */}
      <div className="w-full h-12 flex rounded-lg overflow-hidden">
        <button
          onClick={onGuessHuman}
          className="flex-1 bg-gradient-to-r from-[#2dd4bf] to-[#22d3ee] text-white font-medium hover:from-[#22d3ee] hover:to-[#2dd4bf] transition-all duration-300"
        >
          Guess Human
        </button>
        <button
          onClick={onGuessAI}
          className="flex-1 bg-gradient-to-r from-[#a855f7] to-[#d946ef] text-white font-medium hover:from-[#d946ef] hover:to-[#a855f7] transition-all duration-300"
        >
          Guess AI
        </button>
      </div>
    </div>
  );
};

export default GameControls;
