import React, { useEffect, useState } from 'react';
import socket from '../services/socket';
import { useAuth } from '../contexts/AuthContext';

const SEARCH_TIMEOUT = 10; // 10 seconds before AI fallback

const Game: React.FC = () => {
  const [isSearching, setIsSearching] = useState(false);
  const [gameSession, setGameSession] = useState<any>(null);
  const [countdown, setCountdown] = useState(SEARCH_TIMEOUT);
  const { token } = useAuth();

  useEffect(() => {
    let countdownTimer: NodeJS.Timeout;

    // Set up socket event listeners
    socket.on('connect', () => {
      console.log('Connected to game server');
    });

    socket.on('error', (error) => {
      console.error('Socket error:', error);
      setIsSearching(false);
      setCountdown(SEARCH_TIMEOUT);
    });

    socket.on('game-started', (data) => {
      console.log('Game started:', data);
      setGameSession(data);
      setIsSearching(false);
      setCountdown(SEARCH_TIMEOUT);
    });

    socket.on('queue-status', (data) => {
      console.log('Queue status:', data);
    });

    // Handle countdown timer
    if (isSearching && countdown > 0) {
      countdownTimer = setInterval(() => {
        setCountdown(prev => {
          if (prev <= 1) {
            // Time's up, let the server know to create an AI game
            socket.emit('request-ai-opponent');
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
    }

    // Cleanup
    return () => {
      socket.off('connect');
      socket.off('error');
      socket.off('game-started');
      socket.off('queue-status');
      if (countdownTimer) {
        clearInterval(countdownTimer);
      }
      if (isSearching) {
        socket.emit('leave-queue');
      }
      socket.disconnect();
    };
  }, [isSearching, countdown]);

  const handleFindMatch = () => {
    try {
      setIsSearching(true);
      setCountdown(SEARCH_TIMEOUT);
      // Make sure socket is authenticated and connected
      socket.auth = { token };
      socket.connect();

      // Wait for connection before joining queue
      socket.on('connect', () => {
        console.log('Emitting join-queue');
        socket.emit('join-queue');
      });
    } catch (error) {
      console.error('Error finding match:', error);
      setIsSearching(false);
      setCountdown(SEARCH_TIMEOUT);
    }
  };

  const handleCancelSearch = () => {
    setIsSearching(false);
    setCountdown(SEARCH_TIMEOUT);
    socket.emit('leave-queue');
    console.log('Leaving queue...');
  };

  if (gameSession) {
    return (
      <div className="p-4">
        <h2>Game Session: {gameSession.sessionId}</h2>
        <p>Opponent: {gameSession.opponent.username}</p>
        {/* Add game UI here */}
      </div>
    );
  }

  return (
    <div className="flex flex-col items-center justify-center min-h-[80vh]">
      {!isSearching ? (
        <button
          onClick={handleFindMatch}
          className="px-8 py-4 bg-game-primary text-white rounded-lg hover:bg-game-primary-dark transition-colors"
        >
          Find Match
        </button>
      ) : (
        <div className="text-center">
          <div className="mb-4">
            <div className="text-xl mb-2">Searching for opponent...</div>
            <div className="text-game-light-600">
              {countdown > 0 ? (
                <>
                  Finding human players... {countdown}s
                  <div className="text-sm text-game-light-400">
                    Will connect to AI opponent if no players found
                  </div>
                </>
              ) : (
                "Connecting to AI opponent..."
              )}
            </div>
          </div>
          <button
            onClick={handleCancelSearch}
            className="px-6 py-3 bg-red-500 text-white rounded-lg hover:bg-red-600 transition-colors"
          >
            Cancel
          </button>
        </div>
      )}
    </div>
  );
};

export default Game;
