import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { FadeIn } from '../components/common/Animations';
import { GoogleLogin } from '@react-oauth/google';
import axios from 'axios';
import Spinner from '../components/common/Spinner';

const Login: React.FC = () => {
  const navigate = useNavigate();
  const { login } = useAuth();
  const [error, setError] = useState<string | null>(null);
  const [isLoading, setIsLoading] = useState(false);

  const handleGoogleSuccess = async (credentialResponse: any) => {
    try {
      setIsLoading(true);
      setError(null);

      const response = await axios.post('http://localhost:5000/auth/google', {
        token: credentialResponse.credential
      });

      const { token } = response.data;
      await login(token);
      navigate('/');
    } catch (error: any) {
      console.error('Login failed:', error);
      setError(error.response?.data?.error || 'Failed to login. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-game-dark-900">
      <div className="fixed inset-0 bg-gradient-radial from-game-primary/5 to-transparent pointer-events-none opacity-20" />
      <FadeIn>
        <div className="card max-w-md w-full">
          <h1 className="game-title text-center mb-8">Join the Investigation</h1>
          <p className="text-game-light/70 text-center mb-8">
            Sign in to start unmasking impostors and climb the ranks
          </p>

          {error && (
            <div className="mb-6 p-4 bg-game-error/10 border border-game-error/20 rounded-lg text-game-error text-center">
              {error}
            </div>
          )}

          <div className="flex justify-center">
            {isLoading ? (
              <div className="flex items-center space-x-3">
                <Spinner size="sm" />
                <span className="text-game-light/70">Authenticating...</span>
              </div>
            ) : (
              <GoogleLogin
                onSuccess={handleGoogleSuccess}
                onError={() => {
                  setError('Login failed. Please try again.');
                }}
                useOneTap
                theme="filled_black"
                shape="pill"
                size="large"
                text="continue_with"
                locale="en"
                width="300"
              />
            )}
          </div>

          <p className="mt-6 text-center text-sm text-game-light/60">
            By continuing, you agree to our Terms of Service and Privacy Policy
          </p>
        </div>
      </FadeIn>
    </div>
  );
};

export default Login;
