import React, { useRef, useEffect } from 'react';
import { useGame } from '../../contexts/GameContext';
import Spinner from '../common/Spinner';
import { FadeIn, SlideUp } from '../common/Animations';

interface Message {
  id: string;
  content: string;
  senderId: string;
  timestamp: Date;
}

const ChatWindow: React.FC = () => {
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const { messages, isOpponentTyping } = useGame();

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages, isOpponentTyping]);

  return (
    <div className="bg-white rounded-lg shadow-md h-full flex flex-col backdrop-blur-sm bg-opacity-90">
      <div className="p-4 border-b backdrop-blur-sm bg-white/50">
        <h3 className="text-lg font-semibold">Chat Session</h3>
      </div>

      <div className="flex-1 overflow-y-auto p-4 space-y-4">
        {messages && messages.map((message: Message, index) => (
          <FadeIn key={message.id} delay={index * 100}>
            <div
              className={`flex ${
                message.senderId === 'user' ? 'justify-end' : 'justify-start'
              }`}
            >
              <div
                className={`max-w-[70%] rounded-lg px-4 py-2 animate-messageSlide ${
                  message.senderId === 'user'
                    ? 'bg-primary-600 text-white'
                    : 'bg-gray-200 text-gray-800'
                } shadow-sm hover:shadow-md transition-shadow duration-200`}
              >
                {message.content}
              </div>
            </div>
          </FadeIn>
        ))}

        {isOpponentTyping && (
          <SlideUp>
            <div className="flex justify-start">
              <div className="bg-gray-100 rounded-lg px-4 py-2 flex items-center space-x-2 animate-float">
                <span className="text-gray-600">Typing</span>
                <span className="flex space-x-1">
                  <span className="w-1 h-1 bg-gray-500 rounded-full animate-bounce" style={{ animationDelay: '0ms' }}></span>
                  <span className="w-1 h-1 bg-gray-500 rounded-full animate-bounce" style={{ animationDelay: '150ms' }}></span>
                  <span className="w-1 h-1 bg-gray-500 rounded-full animate-bounce" style={{ animationDelay: '300ms' }}></span>
                </span>
              </div>
            </div>
          </SlideUp>
        )}

        <div ref={messagesEndRef} />
      </div>
    </div>
  );
};

export default ChatWindow;
