import React, { useRef, useEffect } from 'react';
import { useGame } from '../../contexts/GameContext';
import { FadeIn, SlideUp } from '../common/Animations';
import { useAuth } from '../../contexts/AuthContext';

interface Message {
  id: string;
  content: string;
  senderId: string;
  timestamp: Date;
}

const ChatWindow: React.FC = () => {
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const { messages, isOpponentTyping, socketId } = useGame();
  const { user } = useAuth();

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages, isOpponentTyping]);

  const isOwnMessage = (message: Message) => {
    console.log('Checking message ownership:', {
      messageId: message.id,
      senderId: message.senderId,
      userId: user?._id,
      socketId,
      isOwn: user?._id === message.senderId || message.senderId === socketId
    });

    return user?._id === message.senderId || message.senderId === socketId;
  };

  useEffect(() => {
    console.log('Current messages:', messages);
  }, [messages]);

  return (
    <div className="bg-game-dark-800 rounded-lg shadow-dark h-full flex flex-col border border-game-dark-600">
      <div className="p-4 border-b border-game-dark-600 bg-game-dark-700">
        <h3 className="text-lg font-semibold text-game-light">Chat Session</h3>
      </div>

      <div className="flex-1 overflow-y-auto p-4 space-y-4">
        {messages && messages.map((message: Message) => (
          <FadeIn key={message.id}>
            <div
              className={`flex ${
                isOwnMessage(message) ? 'justify-end' : 'justify-start'
              }`}
            >
              <div
                className={`max-w-[70%] rounded-lg px-4 py-2 animate-messageSlide ${
                  isOwnMessage(message)
                    ? 'bg-game-primary text-white'
                    : 'bg-game-dark-700 text-game-light border border-game-dark-600'
                } shadow-dark hover:shadow-dark-hover transition-shadow duration-200`}
              >
                {message.content}
              </div>
            </div>
          </FadeIn>
        ))}

        {isOpponentTyping && (
          <SlideUp>
            <div className="flex justify-start">
              <div className="bg-game-dark-700 rounded-lg px-4 py-2 flex items-center space-x-2 animate-float border border-game-dark-600">
                <span className="text-game-light/70">Typing</span>
                <span className="flex space-x-1">
                  <span className="w-1 h-1 bg-game-light/50 rounded-full animate-bounce" style={{ animationDelay: '0ms' }}></span>
                  <span className="w-1 h-1 bg-game-light/50 rounded-full animate-bounce" style={{ animationDelay: '150ms' }}></span>
                  <span className="w-1 h-1 bg-game-light/50 rounded-full animate-bounce" style={{ animationDelay: '300ms' }}></span>
                </span>
              </div>
            </div>
          </SlideUp>
        )}

        <div ref={messagesEndRef} />
      </div>
    </div>
  );
};

export default ChatWindow;
