import React, { useEffect, useState } from 'react';
import { useGame } from '../contexts/GameContext';
import ChatWindow from '../components/game/ChatWindow';
import GameControls from '../components/game/GameControls';
import GameResult from '../components/game/GameResult';
import Spinner from '../components/common/Spinner';
import { FadeIn } from '../components/common/Animations';

interface GameResult {
  winner?: string;
  forfeitedBy?: string;
  error?: string;
  stats: {
    player1Correct: boolean;
    player2Correct: boolean;
    duration: number;
    messageCount: number;
  };
}

const Game: React.FC = () => {
  const { isPlaying, isLoading, startGame, endGame, isConnected, gameResult, setGameResult } = useGame();
  const [countdown, setCountdown] = useState(3);

  useEffect(() => {
    console.log('Connection status:', isConnected);
  }, [isConnected]);

  useEffect(() => {
    console.log('Game status:', isPlaying ? 'playing' : 'not playing');
  }, [isPlaying]);

  useEffect(() => {
    if (isLoading) {
      const timer = setInterval(() => {
        setCountdown(prev => {
          if (prev <= 1) {
            clearInterval(timer);
            return 0;
          }
          return prev - 1;
        });
      }, 1000);

      return () => clearInterval(timer);
    } else {
      setCountdown(3);
    }
  }, [isLoading]);

  // Cleanup only when unmounting, ignore dependency warning
  useEffect(() => {
    return () => {
      if (isPlaying) {
        console.log('Component unmounting, ending game');
        endGame();
      }
    };
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  const handlePlayAgain = () => {
    setGameResult(null);
    startGame();
  };

  if (!isConnected) {
    return (
      <div className="max-w-4xl mx-auto text-center py-12">
        <h2 className="text-2xl font-bold mb-4 text-game-light">Connecting to server...</h2>
        <p className="text-game-light/70 mb-4">Please wait while we establish connection</p>
        <Spinner size="lg" />
      </div>
    );
  }

  if (isLoading) {
    return (
      <div className="max-w-4xl mx-auto text-center py-12">
        <FadeIn>
          <h2 className="text-2xl font-bold mb-4 text-game-light">Initializing Game Session</h2>
          <div className="text-6xl font-bold text-game-primary mb-6">{countdown}</div>
          <div className="flex justify-center mb-8">
            <Spinner size="lg" />
          </div>
          <p className="text-game-light/70">
            Preparing your chat session...
          </p>
        </FadeIn>
      </div>
    );
  }

  if (!isPlaying && !gameResult) {
    return (
      <div className="max-w-4xl mx-auto text-center py-12">
        <h2 className="text-2xl font-bold mb-4 text-game-light">Ready to Play?</h2>
        <p className="text-game-light/70 mb-6">Click 'Find Match' to start a conversation and test your intuition!</p>
        <button
          onClick={startGame}
          className="btn btn-primary text-lg px-12 py-4"
        >
          Find Match
        </button>
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto py-8">
      <div className="grid grid-rows-[1fr,auto] gap-4 h-[calc(100vh-12rem)]">
        <ChatWindow />
        <GameControls />
      </div>
      {gameResult && (
        <GameResult 
          result={gameResult} 
          onPlayAgain={handlePlayAgain} 
        />
      )}
    </div>
  );
};

export default Game;
