import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import axios from 'axios';

const Login: React.FC = () => {
  const { login } = useAuth();
  const navigate = useNavigate();

  const handleAutoLogin = async () => {
    try {
      const response = await axios.post('http://localhost:5001/auth/auto-login');

      if (response.data.success && response.data.token) {
        await login(response.data.token);
        navigate('/');
      } else {
        throw new Error('Invalid response from server');
      }
    } catch (error) {
      console.error('Auto-login failed:', error);
    }
  };

  return (
    <div className="flex items-center justify-center min-h-[80vh]">
      <div className="bg-game-light p-8 rounded-lg shadow-lg w-full max-w-md">
        <h2 className="text-3xl font-bold mb-6 text-center text-game-dark">
          Welcome to Human or AI?
        </h2>
        <p className="text-game-dark-600 mb-8 text-center">
          Test your ability to distinguish between human and AI responses in real-time chat.
        </p>
        <div className="space-y-6">
          <button
            onClick={handleAutoLogin}
            className="w-full bg-game-primary hover:bg-game-primary-dark text-white font-bold py-3 px-6 rounded-lg transition-colors duration-200"
          >
            Start Playing
          </button>
          <div className="text-center text-game-dark-400 text-sm">
            No account needed - just click and play!
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
