import React, { useState, useEffect, useRef } from 'react';
import socket from '../services/socket';

interface Message {
  senderId: string;
  content: string;
  timestamp: Date;
}

interface ChatWindowProps {
  sessionId: string;
  opponent: {
    id: string;
    username: string;
  };
  onGuess: (guessedAI: boolean) => void;
}

const ChatWindow: React.FC<ChatWindowProps> = ({ sessionId, opponent, onGuess }) => {
  const [messages, setMessages] = useState<Message[]>([]);
  const [newMessage, setNewMessage] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    socket.on('new-message', (message: Message) => {
      setMessages(prev => [...prev, message]);
    });

    socket.on('opponent-typing', () => {
      setIsTyping(true);
      setTimeout(() => setIsTyping(false), 1000);
    });

    return () => {
      socket.off('new-message');
      socket.off('opponent-typing');
    };
  }, []);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSendMessage = (e: React.FormEvent) => {
    e.preventDefault();
    if (!newMessage.trim()) return;

    socket.emit('send-message', {
      sessionId,
      content: newMessage.trim()
    });

    setNewMessage('');
  };

  return (
    <div className="flex flex-col h-[600px] bg-game-dark-600/50 rounded-lg p-4">
      <div className="flex justify-between items-center mb-4">
        <h3 className="text-xl font-bold">Chat with {opponent.username}</h3>
        <div className="flex gap-2">
          <button
            onClick={() => onGuess(true)}
            className="px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600"
          >
            Guess AI
          </button>
          <button
            onClick={() => onGuess(false)}
            className="px-4 py-2 bg-green-500 text-white rounded hover:bg-green-600"
          >
            Guess Human
          </button>
        </div>
      </div>

      <div className="flex-1 overflow-y-auto mb-4 space-y-2">
        {messages.map((message, index) => (
          <div
            key={index}
            className={`p-2 rounded-lg max-w-[80%] ${
              message.senderId === socket.id
                ? 'ml-auto bg-game-primary text-white'
                : 'bg-game-dark-400 text-game-light'
            }`}
          >
            {message.content}
          </div>
        ))}
        {isTyping && (
          <div className="text-game-light-600 text-sm">Opponent is typing...</div>
        )}
        <div ref={messagesEndRef} />
      </div>

      <form onSubmit={handleSendMessage} className="flex gap-2">
        <input
          type="text"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          className="flex-1 bg-game-dark-400 text-game-light rounded px-4 py-2"
          placeholder="Type your message..."
        />
        <button
          type="submit"
          className="px-4 py-2 bg-game-primary text-white rounded hover:bg-game-primary-dark"
        >
          Send
        </button>
      </form>
    </div>
  );
};

export default ChatWindow;
