import React from 'react';
import { useAuth } from '../contexts/AuthContext';

const Profile: React.FC = () => {
  const { user } = useAuth();

  if (!user) {
    return <div>Loading...</div>;
  }

  return (
    <div className="max-w-4xl mx-auto">
      <div className="bg-white rounded-lg shadow-md p-6">
        <div className="flex items-center space-x-4 mb-6">
          <div className="w-20 h-20 rounded-full bg-gray-200 flex items-center justify-center">
            {user.avatar ? (
              <img src={user.avatar} alt={user.username} className="w-full h-full rounded-full" />
            ) : (
              <span className="text-2xl">ðŸ‘¤</span>
            )}
          </div>
          <div>
            <h2 className="text-2xl font-bold">{user.username}</h2>
            <p className="text-gray-600">{user.email}</p>
          </div>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div className="bg-gray-50 rounded-lg p-4">
            <h3 className="text-lg font-semibold mb-2">Statistics</h3>
            <div className="space-y-2">
              <p>Total Games: {user.stats.totalGames}</p>
              <p>Games Won: {user.stats.gamesWon}</p>
              <p>Win Rate: {user.stats.winRate}%</p>
              <p>Correct Guesses: {user.stats.correctGuesses}</p>
              <p>Successful Deceptions: {user.stats.successfulDeceptions}</p>
            </div>
          </div>

          <div className="bg-gray-50 rounded-lg p-4">
            <h3 className="text-lg font-semibold mb-2">Achievements</h3>
            <div className="text-gray-600">
              Coming soon...
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
