import React, { useEffect, useState } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { useSocket } from '../contexts/SocketContext';
import ChatWindow from '../components/game/ChatWindow';

interface GameStartData {
  sessionId: string;
  opponent: {
    id: string;
    isAI: boolean;
  };
}

interface GameResult {
  player1: {
    userId: string;
    guess: 'human' | 'ai';
    result: {
      correct: boolean;
      guess: 'human' | 'ai';
      actualType: 'human' | 'ai';
      score: {
        total: number;
        breakdown: {
          basePoints: number;
          messageBonus: number;
          timeBonus: number;
          deceptionBonus: number;
        };
        multiplier: number;
      };
      stats: {
        messageCount: number;
        duration: number;
        averageResponseTime: number;
      };
    };
  };
  player2: {
    userId: string;
    guess: 'human' | 'ai';
    result: {
      correct: boolean;
      guess: 'human' | 'ai';
      actualType: 'human' | 'ai';
      score: {
        total: number;
        breakdown: {
          basePoints: number;
          messageBonus: number;
          timeBonus: number;
          deceptionBonus: number;
        };
        multiplier: number;
      };
      stats: {
        messageCount: number;
        duration: number;
        averageResponseTime: number;
      };
    };
  };
  sessionStats: {
    messageCount: number;
    duration: number;
    averageResponseTime: number;
  };
}

interface WaitingState {
  isWaiting: boolean;
  message: string;
}

const Game: React.FC = () => {
  const { user } = useAuth();
  const socket = useSocket();
  const [inQueue, setInQueue] = useState(false);
  const [inGame, setInGame] = useState(false);
  const [countdown, setCountdown] = useState<number | null>(null);
  const [gameResult, setGameResult] = useState<GameResult | null>(null);
  const [waitingForGuess, setWaitingForGuess] = useState<WaitingState>({
    isWaiting: false,
    message: ''
  });

  useEffect(() => {
    if (socket) {
      socket.on('queue-joined', () => {
        setInQueue(true);
        setGameResult(null);
        // Start countdown from 10
        setCountdown(10);
      });

      socket.on('game-started', (data: GameStartData) => {
        setInQueue(false);
        setInGame(true);
        setCountdown(null);
        console.log('Game started:', data);
      });

      socket.on('game-result', (result: GameResult) => {
        setGameResult(result);
        setInGame(false);
      });

      socket.on('game-ended', () => {
        setInGame(false);
      });

      return () => {
        socket.off('queue-joined');
        socket.off('game-started');
        socket.off('game-result');
        socket.off('game-ended');
      };
    }
  }, [socket]);

  // Countdown effect
  useEffect(() => {
    if (countdown !== null && countdown > 0) {
      const timer = setInterval(() => {
        setCountdown(prev => prev !== null ? prev - 1 : null);
      }, 1000);
      return () => clearInterval(timer);
    }
  }, [countdown]);

  const joinQueue = () => {
    if (socket) {
      socket.emit('join-queue');
    }
  };

  const playAgain = () => {
    setGameResult(null);
    joinQueue();
  };

  return (
    <div className="container mx-auto px-4">
      <div className="bg-game-dark-600 rounded-lg shadow-xl p-6">
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-3xl font-bold text-game-light">Welcome, {user?.username}!</h1>
          {inGame && (
            <div className="flex space-x-4">
              <button
                onClick={() => socket?.emit('make-guess', 'human')}
                className="bg-game-secondary hover:bg-green-600 text-white px-6 py-2 rounded-lg transition-colors"
              >
                Guess Human
              </button>
              <button
                onClick={() => socket?.emit('make-guess', 'ai')}
                className="bg-game-primary hover:bg-game-primary-dark text-white px-6 py-2 rounded-lg transition-colors"
              >
                Guess AI
              </button>
            </div>
          )}
        </div>

        {!inQueue && !inGame && !gameResult && (
          <div className="text-center py-12">
            <button
              onClick={joinQueue}
              className="bg-game-primary hover:bg-game-primary-dark text-white font-bold py-3 px-8 rounded-lg text-lg transition-all duration-200 transform hover:scale-[1.02]"
            >
              Find Match
            </button>
          </div>
        )}

        {inQueue && (
          <div className="text-center py-12">
            <div className="mb-4 text-4xl font-bold text-game-primary">
              {countdown}
            </div>
            <div className="animate-pulse text-xl text-game-light">
              Looking for a match...
            </div>
          </div>
        )}

        {inGame && <ChatWindow />}

        {gameResult && (
          <div className="mt-6 p-6 bg-game-dark-400/50 rounded-lg backdrop-blur-sm border border-game-dark-300/50">
            <h2 className="text-2xl font-bold mb-6 text-game-light">Game Results</h2>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
              {/* Player 1 Results */}
              <div className="bg-game-dark-600/50 p-6 rounded-lg">
                <h3 className="text-xl font-bold text-game-primary mb-4">
                  {gameResult.player1.userId === user?.id ? 'Your' : 'Opponent\'s'} Results
                </h3>
                {/* Add player 1 result details */}
              </div>

              {/* Player 2 Results */}
              <div className="bg-game-dark-600/50 p-6 rounded-lg">
                <h3 className="text-xl font-bold text-game-primary mb-4">
                  {gameResult.player2.userId === user?.id ? 'Your' : 'Opponent\'s'} Results
                </h3>
                {/* Add player 2 result details */}
              </div>
            </div>

            {/* Session Stats */}
            <div className="mt-8 bg-game-dark-600/50 p-6 rounded-lg">
              <h3 className="text-xl font-bold text-game-primary mb-4">Session Stats</h3>
              <div className="grid grid-cols-3 gap-4">
                <div>
                  <p className="text-game-light-600">Messages</p>
                  <p className="text-2xl font-bold text-game-light">
                    {gameResult.sessionStats.messageCount}
                  </p>
                </div>
                <div>
                  <p className="text-game-light-600">Duration</p>
                  <p className="text-2xl font-bold text-game-light">
                    {Math.floor(gameResult.sessionStats.duration / 60)}m {gameResult.sessionStats.duration % 60}s
                  </p>
                </div>
                <div>
                  <p className="text-game-light-600">Avg Response</p>
                  <p className="text-2xl font-bold text-game-light">
                    {gameResult.sessionStats.averageResponseTime.toFixed(1)}s
                  </p>
                </div>
              </div>
            </div>

            {/* Play Again Button */}
            <div className="text-center mt-8">
              <button
                onClick={playAgain}
                className="bg-game-primary hover:bg-game-primary-dark text-white font-bold py-3 px-8 rounded-lg transition-all duration-200 transform hover:scale-[1.02]"
              >
                Play Again
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Game;
