import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { FadeIn } from '../components/common/Animations';
import axios from 'axios';
import Spinner from '../components/common/Spinner';

const Login: React.FC = () => {
  const navigate = useNavigate();
  const { login, user } = useAuth();
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    if (user) {
      navigate('/game');
    }
  }, [user, navigate]);

  const handleAutoLogin = async () => {
    try {
      setIsLoading(true);
      setError(null);

      const response = await axios.post('/auth/auto-login');
      console.log('Auto-login response:', response.data);

      await login(response.data.token);
      navigate('/game', { replace: true });
    } catch (error: any) {
      console.error('Auto-login failed:', error);
      setError('Failed to create game session. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    if (!user) {
      handleAutoLogin();
    }
  }, []);

  return (
    <div className="min-h-screen flex items-center justify-center bg-game-dark-900">
      <div className="fixed inset-0 bg-gradient-radial from-game-primary/5 to-transparent pointer-events-none opacity-20" />
      <FadeIn>
        <div className="card max-w-md w-full text-center">
          <h1 className="game-title mb-8">Welcome to the Game</h1>

          {error ? (
            <>
              <p className="text-game-error mb-6">{error}</p>
              <button 
                onClick={handleAutoLogin}
                className="btn btn-primary w-full"
                disabled={isLoading}
              >
                Try Again
              </button>
            </>
          ) : (
            <div className="flex flex-col items-center">
              <Spinner size="lg" />
              <p className="mt-4 text-game-light/70">
                Creating your game session...
              </p>
            </div>
          )}
        </div>
      </FadeIn>
    </div>
  );
};

export default Login;
