import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const Navbar: React.FC = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <nav className="bg-game-dark-600 border-b border-game-dark-400">
      <div className="container mx-auto px-4">
        <div className="flex justify-between items-center h-16">
          <div className="flex items-center space-x-8">
            <Link to="/" className="flex items-center space-x-2">
              <span className="text-2xl font-bold text-game-primary">Unmask</span>
              <span className="text-2xl font-light text-game-light">the Impostor</span>
            </Link>

            {user && (
              <div className="hidden md:flex space-x-4">
                <Link 
                  to="/" 
                  className="text-game-light-600 hover:text-game-primary transition-colors"
                >
                  Play
                </Link>
                <Link 
                  to="/leaderboard" 
                  className="text-game-light-600 hover:text-game-primary transition-colors"
                >
                  Leaderboard
                </Link>
                <Link 
                  to="/profile" 
                  className="text-game-light-600 hover:text-game-primary transition-colors"
                >
                  Profile
                </Link>
              </div>
            )}
          </div>

          {user && (
            <div className="flex items-center space-x-4">
              <div className="text-game-light-600">
                <span className="mr-2">Points:</span>
                <span className="text-game-primary font-semibold">
                  {user.stats.totalPoints}
                </span>
              </div>
              <button
                onClick={handleLogout}
                className="bg-game-primary hover:bg-game-primary-dark text-white px-4 py-2 rounded-lg transition-colors"
              >
                Logout
              </button>
            </div>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
