import React, { useState } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const Navbar: React.FC = () => {
  const { user, logout } = useAuth();
  const location = useLocation();
  const navigate = useNavigate();
  const [showLogoutConfirm, setShowLogoutConfirm] = useState(false);

  const handleLogout = () => {
    setShowLogoutConfirm(true);
  };

  const confirmLogout = () => {
    logout();
    navigate('/');
    setShowLogoutConfirm(false);
  };

  const navItems = [
    { path: '/', label: 'Home' },
    { path: '/game', label: 'Play Game' },
    { path: '/leaderboard', label: 'Leaderboard' },
  ];

  return (
    <>
      <nav className="glass-panel sticky top-0 z-50">
        <div className="container mx-auto px-4">
          <div className="flex justify-between items-center h-16">
            <Link to="/" className="game-title">
              Unmask the Impostor
            </Link>

            <div className="flex items-center space-x-2">
              {navItems.map((item) => (
                <Link
                  key={item.path}
                  to={item.path}
                  className={`px-4 py-2 rounded-xl text-sm font-medium transition-all duration-300 ${
                    location.pathname === item.path
                      ? 'bg-gradient-game text-white shadow-game'
                      : 'text-game-light hover:bg-game-dark-700'
                  }`}
                >
                  {item.label}
                </Link>
              ))}
              {user ? (
                <div className="flex items-center space-x-2 ml-4">
                  <Link
                    to="/profile"
                    className="flex items-center space-x-2 px-4 py-2 rounded-xl hover:bg-game-dark-700 transition-colors"
                  >
                    {user.avatar ? (
                      <img src={user.avatar} alt="" className="w-8 h-8 rounded-full" />
                    ) : (
                      <div className="w-8 h-8 rounded-full bg-game-primary/10 flex items-center justify-center">
                        <span className="text-game-primary">{user.username[0]}</span>
                      </div>
                    )}
                    <span className="text-game-light font-medium">{user.username}</span>
                  </Link>
                  <button
                    onClick={handleLogout}
                    className="btn btn-secondary text-sm"
                  >
                    Logout
                  </button>
                </div>
              ) : (
                <Link
                  to="/login"
                  className="btn btn-primary text-sm"
                >
                  Login
                </Link>
              )}
            </div>
          </div>
        </div>
      </nav>

      {/* Logout Confirmation Modal */}
      {showLogoutConfirm && (
        <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50">
          <div className="card max-w-sm mx-4">
            <h3 className="text-xl font-bold mb-4 text-game-light">Confirm Logout</h3>
            <p className="text-game-light/70 mb-6">Are you sure you want to logout?</p>
            <div className="flex justify-end space-x-3">
              <button 
                onClick={() => setShowLogoutConfirm(false)}
                className="btn btn-secondary"
              >
                Cancel
              </button>
              <button 
                onClick={confirmLogout}
                className="btn btn-primary"
              >
                Logout
              </button>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default Navbar;
