import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { FadeIn } from '../components/common/Animations';
import axios from 'axios';
import Spinner from '../components/common/Spinner';

const Login: React.FC = () => {
  const navigate = useNavigate();
  const { login } = useAuth();
  const [username, setUsername] = useState('');
  const [error, setError] = useState<string | null>(null);
  const [isLoading, setIsLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      setIsLoading(true);
      setError(null);

      const response = await axios.post('http://localhost:5000/auth/login', {
        username: username.trim()
      });

      const { token } = response.data;
      await login(token);
      navigate('/');
    } catch (error: any) {
      console.error('Login failed:', error);
      setError(error.response?.data?.error || 'Failed to login. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-game-dark-900">
      <div className="fixed inset-0 bg-gradient-radial from-game-primary/5 to-transparent pointer-events-none opacity-20" />
      <FadeIn>
        <div className="card max-w-md w-full">
          <h1 className="game-title text-center mb-8">Join the Investigation</h1>
          <p className="text-game-light/70 text-center mb-8">
            Enter a username to start playing
          </p>

          {error && (
            <div className="mb-6 p-4 bg-game-error/10 border border-game-error/20 rounded-lg text-game-error text-center">
              {error}
            </div>
          )}

          <form onSubmit={handleSubmit} className="space-y-4">
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="Enter username"
              className="input w-full"
              required
              minLength={3}
              maxLength={20}
            />

            <button
              type="submit"
              className="btn btn-primary w-full"
              disabled={isLoading}
            >
              {isLoading ? <Spinner size="sm" /> : 'Start Playing'}
            </button>
          </form>
        </div>
      </FadeIn>
    </div>
  );
};

export default Login;
