import React from 'react';
import { useNavigate } from 'react-router-dom';
import { FadeIn } from '../common/Animations';

interface GameResultsProps {
  result: {
    winner?: string;
    score?: number;
    forfeitedBy?: string;
    error?: string;
    stats: {
      messageCount: number;
      duration: number;
      player1Correct: boolean;
      player2Correct: boolean;
      guesses?: {
        player: {
          guessedAI: boolean;
          timestamp: Date;
        };
        opponent: {
          guessedAI: boolean;
          timestamp: Date;
        };
      };
      isAIOpponent?: boolean;
    };
  };
  onPlayAgain: () => void;
}

const GameResults: React.FC<GameResultsProps> = ({ result, onPlayAgain }) => {
  const navigate = useNavigate();

  if (result.error) {
    return (
      <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50">
        <FadeIn>
          <div className="bg-game-dark-800 rounded-lg p-6 max-w-md w-full mx-4">
            <h3 className="text-xl font-bold mb-4 text-game-light">Game Ended</h3>
            <p className="text-game-light-600 mb-6">{result.error}</p>
            <div className="flex justify-end space-x-4">
              <button 
                onClick={() => navigate('/')} 
                className="px-4 py-2 bg-game-dark-600 text-game-light rounded hover:bg-game-dark-500 transition-colors"
              >
                Home
              </button>
              <button 
                onClick={onPlayAgain} 
                className="px-4 py-2 bg-game-primary text-white rounded hover:bg-game-primary-dark transition-colors"
              >
                Play Again
              </button>
            </div>
          </div>
        </FadeIn>
      </div>
    );
  }

  return (
    <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50">
      <FadeIn>
        <div className="bg-game-dark-800 rounded-lg p-8 max-w-md w-full mx-4">
          {/* Victory/Loss Header */}
          <div className="text-center mb-6">
            <div className="text-5xl mb-2">
              {result.score ? 'ðŸŽ‰' : 'ðŸ˜…'}
            </div>
            <h2 className="text-2xl font-bold text-game-light">
              {result.score ? 'Victory!' : 'Good Try!'}
            </h2>
            {result.score && (
              <div className="text-3xl font-bold text-game-success mt-2">
                +{result.score} points
              </div>
            )}
          </div>

          {/* Game Stats */}
          <div className="bg-game-dark-700 p-4 rounded-lg mb-6">
            <h3 className="text-lg font-semibold text-game-light mb-4">Game Stats</h3>
            <div className="grid grid-cols-2 gap-4">
              <div className="text-center p-3 bg-game-dark-600 rounded">
                <div className="text-xl font-bold text-game-light">
                  {Math.floor(result.stats.duration / 60)}:{(result.stats.duration % 60).toString().padStart(2, '0')}
                </div>
                <div className="text-sm text-game-light-600">Duration</div>
              </div>
              <div className="text-center p-3 bg-game-dark-600 rounded">
                <div className="text-xl font-bold text-game-light">
                  {result.stats.messageCount}
                </div>
                <div className="text-sm text-game-light-600">Messages</div>
              </div>
            </div>
          </div>

          {/* Action Buttons */}
          <div className="flex justify-between space-x-4">
            <button 
              onClick={() => navigate('/')} 
              className="flex-1 px-4 py-3 bg-game-dark-600 text-game-light rounded hover:bg-game-dark-500 transition-colors"
            >
              Home
            </button>
            <button 
              onClick={onPlayAgain} 
              className="flex-1 px-4 py-3 bg-[#9333EA] text-white rounded hover:bg-[#7E22CE] transition-colors"
            >
              Play Again
            </button>
          </div>
        </div>
      </FadeIn>
    </div>
  );
};

export default GameResults;
