import React, { useEffect, useState } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { useSocket } from '../contexts/SocketContext';
import ChatWindow from '../components/game/ChatWindow';

interface GameStartData {
  sessionId: string;
  opponent: {
    id: string;
    isAI: boolean;
  };
}

interface GameResult {
  correct: boolean;
  guess: 'human' | 'ai';
  actualType: 'human' | 'ai';
  score: {
    total: number;
    breakdown: {
      basePoints: number;
      messageBonus: number;
      timeBonus: number;
      deceptionBonus: number;
    };
    multiplier: number;
  };
  stats: {
    messageCount: number;
    duration: number;
    averageResponseTime: number;
  };
}

const Game: React.FC = () => {
  const { user } = useAuth();
  const socket = useSocket();
  const [inQueue, setInQueue] = useState(false);
  const [inGame, setInGame] = useState(false);
  const [countdown, setCountdown] = useState<number | null>(null);
  const [gameResult, setGameResult] = useState<GameResult | null>(null);

  useEffect(() => {
    if (socket) {
      socket.on('queue-joined', () => {
        setInQueue(true);
        setGameResult(null);
        // Start countdown from 10
        setCountdown(10);
      });

      socket.on('game-started', (data: GameStartData) => {
        setInQueue(false);
        setInGame(true);
        setCountdown(null);
        console.log('Game started:', data);
      });

      socket.on('game-result', (result: GameResult) => {
        setGameResult(result);
        setInGame(false);
      });

      socket.on('game-ended', () => {
        setInGame(false);
      });

      return () => {
        socket.off('queue-joined');
        socket.off('game-started');
        socket.off('game-result');
        socket.off('game-ended');
      };
    }
  }, [socket]);

  // Countdown effect
  useEffect(() => {
    if (countdown !== null && countdown > 0) {
      const timer = setInterval(() => {
        setCountdown(prev => prev !== null ? prev - 1 : null);
      }, 1000);
      return () => clearInterval(timer);
    }
  }, [countdown]);

  const joinQueue = () => {
    if (socket) {
      socket.emit('join-queue');
    }
  };

  const playAgain = () => {
    setGameResult(null);
    joinQueue();
  };

  return (
    <div className="container mx-auto px-4">
      <div className="bg-game-dark-600 rounded-lg shadow-xl p-6">
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-3xl font-bold text-game-light">Welcome, {user?.username}!</h1>
          {inGame && (
            <div className="flex space-x-4">
              <button
                onClick={() => socket?.emit('make-guess', 'human')}
                className="bg-game-secondary hover:bg-green-600 text-white px-6 py-2 rounded-lg transition-colors"
              >
                Guess Human
              </button>
              <button
                onClick={() => socket?.emit('make-guess', 'ai')}
                className="bg-game-primary hover:bg-game-primary-dark text-white px-6 py-2 rounded-lg transition-colors"
              >
                Guess AI
              </button>
            </div>
          )}
        </div>

        {!inQueue && !inGame && !gameResult && (
          <div className="text-center py-12">
            <button
              onClick={joinQueue}
              className="bg-game-primary hover:bg-game-primary-dark text-white font-bold py-3 px-8 rounded-lg text-lg transition-all duration-200 transform hover:scale-[1.02]"
            >
              Find Match
            </button>
          </div>
        )}

        {inQueue && (
          <div className="text-center py-12">
            <div className="mb-4 text-4xl font-bold text-game-primary">
              {countdown}
            </div>
            <div className="animate-pulse text-xl text-game-light">
              Looking for a match...
            </div>
          </div>
        )}

        {inGame && <ChatWindow />}

        {gameResult && (
          <div className="mt-6 p-6 bg-game-dark-400/50 rounded-lg backdrop-blur-sm border border-game-dark-300/50">
            <h2 className="text-2xl font-bold mb-4 text-game-light">Game Results</h2>
            <div className="space-y-6">
              <div className="text-xl">
                Your guess was{' '}
                <span className={gameResult.correct ? 'text-green-500' : 'text-red-500 font-bold'}>
                  {gameResult.correct ? 'correct!' : 'incorrect!'}
                </span>
                <div className="text-game-light-600 mt-2">
                  The opponent was {gameResult.actualType === 'ai' ? 'an AI' : 'a human'} player.
                </div>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div className="bg-game-dark-600/50 p-4 rounded-lg">
                  <h3 className="font-semibold text-game-primary mb-2">Score Breakdown</h3>
                  <ul className="space-y-2 text-game-light-600">
                    <li className="flex justify-between">
                      <span>Base Points:</span>
                      <span className="text-game-light">{gameResult.score.breakdown.basePoints}</span>
                    </li>
                    <li className="flex justify-between">
                      <span>Message Bonus:</span>
                      <span className="text-game-light">+{gameResult.score.breakdown.messageBonus}</span>
                    </li>
                    <li className="flex justify-between">
                      <span>Time Bonus:</span>
                      <span className="text-game-light">+{gameResult.score.breakdown.timeBonus}</span>
                    </li>
                    <li className="flex justify-between">
                      <span>Deception Bonus:</span>
                      <span className="text-game-light">+{gameResult.score.breakdown.deceptionBonus}</span>
                    </li>
                    <li className="flex justify-between border-t border-game-dark-400 pt-2 mt-2">
                      <span>Multiplier:</span>
                      <span className="text-game-primary">Ã—{gameResult.score.multiplier}</span>
                    </li>
                    <li className="flex justify-between border-t border-game-dark-400 pt-2 mt-2 font-bold">
                      <span>Total Score:</span>
                      <span className="text-game-primary">{gameResult.score.total}</span>
                    </li>
                  </ul>
                </div>

                <div className="bg-game-dark-600/50 p-4 rounded-lg">
                  <h3 className="font-semibold text-game-primary mb-2">Game Stats</h3>
                  <ul className="space-y-2 text-game-light-600">
                    <li className="flex justify-between">
                      <span>Messages Exchanged:</span>
                      <span className="text-game-light">{gameResult.stats.messageCount}</span>
                    </li>
                    <li className="flex justify-between">
                      <span>Game Duration:</span>
                      <span className="text-game-light">
                        {Math.floor(gameResult.stats.duration / 60)}m {gameResult.stats.duration % 60}s
                      </span>
                    </li>
                    <li className="flex justify-between">
                      <span>Avg Response Time:</span>
                      <span className="text-game-light">
                        {gameResult.stats.averageResponseTime.toFixed(1)}s
                      </span>
                    </li>
                  </ul>
                </div>
              </div>

              <div className="text-center mt-8">
                <button
                  onClick={playAgain}
                  className="bg-game-primary hover:bg-game-primary-dark text-white font-bold py-3 px-8 rounded-lg transition-all duration-200 transform hover:scale-[1.02]"
                >
                  Play Again
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Game;
