import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { FadeIn } from '../components/common/Animations';

const Login: React.FC = () => {
  const navigate = useNavigate();
  const { login } = useAuth();

  const handleGoogleLogin = async () => {
    try {
      // TODO: Implement Google OAuth
      const token = 'dummy-token'; // This will come from Google OAuth
      await login(token);
      navigate('/');
    } catch (error) {
      console.error('Login failed:', error);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-game-dark-900">
      <div className="fixed inset-0 bg-gradient-radial from-game-primary/5 to-transparent pointer-events-none opacity-20" />
      <FadeIn>
        <div className="card max-w-md w-full">
          <h1 className="game-title text-center mb-8">Welcome Back!</h1>

          <button
            onClick={handleGoogleLogin}
            className="w-full btn btn-secondary flex items-center justify-center space-x-2 group"
          >
            <img 
              src="/google-icon.png" 
              alt="Google" 
              className="w-5 h-5"
            />
            <span className="text-game-light group-hover:text-white transition-colors">
              Continue with Google
            </span>
          </button>

          <p className="mt-4 text-center text-sm text-game-light/60">
            By continuing, you agree to our Terms of Service and Privacy Policy
          </p>
        </div>
      </FadeIn>
    </div>
  );
};

export default Login;
