import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FadeIn } from '../components/common/Animations';
import Spinner from '../components/common/Spinner';

interface LeaderboardEntry {
  _id: string;
  username: string;
  avatar?: string;
  stats: {
    totalGames: number;
    gamesWon: number;
    winRate: number;
    correctGuesses: number;
  };
}

const Leaderboard: React.FC = () => {
  const [leaderboard, setLeaderboard] = useState<LeaderboardEntry[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchLeaderboard = async () => {
      try {
        const response = await axios.get('/game/leaderboard');
        setLeaderboard(response.data);
      } catch (error) {
        console.error('Failed to fetch leaderboard:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchLeaderboard();
  }, []);

  if (loading) {
    return (
      <div className="flex justify-center items-center h-[60vh]">
        <Spinner size="lg" />
      </div>
    );
  }

  return (
    <FadeIn>
      <div className="max-w-4xl mx-auto">
        <h2 className="text-2xl font-bold mb-6 text-game-light">Global Leaderboard</h2>

        <div className="card overflow-hidden">
          <table className="min-w-full">
            <thead className="bg-game-dark-700">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-game-light/70 uppercase tracking-wider">Rank</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-game-light/70 uppercase tracking-wider">Player</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-game-light/70 uppercase tracking-wider">Games</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-game-light/70 uppercase tracking-wider">Win Rate</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-game-light/70 uppercase tracking-wider">Correct Guesses</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-game-dark-600">
              {leaderboard.map((entry, index) => (
                <tr key={entry._id} className="hover:bg-game-dark-700/50 transition-colors">
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-game-light/70">#{index + 1}</td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="flex items-center">
                      {entry.avatar ? (
                        <img className="h-8 w-8 rounded-full mr-3" src={entry.avatar} alt="" />
                      ) : (
                        <div className="h-8 w-8 rounded-full mr-3 bg-game-dark-600 flex items-center justify-center">
                          <span className="text-game-light">{entry.username[0]}</span>
                        </div>
                      )}
                      <div className="text-sm font-medium text-game-light">{entry.username}</div>
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-game-light/70">{entry.stats.totalGames}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-game-light/70">{entry.stats.winRate}%</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-game-light/70">{entry.stats.correctGuesses}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </FadeIn>
  );
};

export default Leaderboard;
