import React, { useState } from 'react';
import { useGame } from '../../contexts/GameContext';
import { PulseEffect } from '../common/Animations';

const GameControls: React.FC = () => {
  const [message, setMessage] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const { sendMessage, makeGuess } = useGame();

  const handleSend = () => {
    if (message.trim()) {
      sendMessage(message);
      setMessage('');
      setIsTyping(false);
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
    setMessage(e.target.value);
    if (!isTyping && e.target.value.trim()) {
      setIsTyping(true);
    } else if (isTyping && !e.target.value.trim()) {
      setIsTyping(false);
    }
  };

  return (
    <div className="bg-white rounded-lg shadow-md p-4 backdrop-blur-sm bg-opacity-90">
      <div className="flex space-x-4 mb-4">
        <div className="flex-1 relative">
          <textarea
            value={message}
            onChange={handleChange}
            onKeyPress={handleKeyPress}
            className="flex-1 input resize-none transition-all duration-200 focus:shadow-lg"
            placeholder="Type your message..."
            rows={2}
          />
          {isTyping && <PulseEffect className="border border-primary-200" />}
        </div>
        <button
          onClick={handleSend}
          className="btn btn-primary self-end relative overflow-hidden group"
        >
          <span className="relative z-10">Send</span>
          <div className="absolute inset-0 bg-primary-700 transform scale-x-0 group-hover:scale-x-100 transition-transform origin-left"></div>
        </button>
      </div>

      <div className="flex justify-between">
        <button
          onClick={() => makeGuess('human')}
          className="btn relative overflow-hidden group bg-yellow-500 text-white hover:bg-yellow-600"
        >
          <span className="relative z-10">Guess Human</span>
          <div className="absolute inset-0 bg-yellow-600 transform scale-x-0 group-hover:scale-x-100 transition-transform origin-left"></div>
        </button>
        <button
          onClick={() => makeGuess('ai')}
          className="btn relative overflow-hidden group bg-purple-500 text-white hover:bg-purple-600"
        >
          <span className="relative z-10">Guess AI</span>
          <div className="absolute inset-0 bg-purple-600 transform scale-x-0 group-hover:scale-x-100 transition-transform origin-left"></div>
        </button>
      </div>
    </div>
  );
};

export default GameControls;
