import React, { useEffect, useState } from 'react';
import socket from '../services/socket';
import { useAuth } from '../contexts/AuthContext';
import ChatWindow from '../components/ChatWindow';

const SEARCH_TIMEOUT = 10; // 10 seconds before AI fallback

const Game: React.FC = () => {
  const [isSearching, setIsSearching] = useState(false);
  const [gameSession, setGameSession] = useState<any>(null);
  const { token } = useAuth();

  useEffect(() => {
    let searchTimeout: NodeJS.Timeout;

    // Set up socket event listeners
    socket.on('connect', () => {
      console.log('Connected to game server');
    });

    socket.on('error', (error) => {
      console.error('Socket error:', error);
      setIsSearching(false);
    });

    socket.on('game-started', (data) => {
      console.log('Game started:', data);
      setGameSession(data);
      setIsSearching(false);
      if (searchTimeout) {
        clearTimeout(searchTimeout);
      }
    });

    socket.on('queue-status', (data) => {
      console.log('Queue status:', data);
    });

    // If searching, set timeout for AI fallback
    if (isSearching) {
      searchTimeout = setTimeout(() => {
        socket.emit('request-ai-opponent');
      }, SEARCH_TIMEOUT * 1000);
    }

    // Cleanup
    return () => {
      socket.off('connect');
      socket.off('error');
      socket.off('game-started');
      socket.off('queue-status');
      if (searchTimeout) {
        clearTimeout(searchTimeout);
      }
      if (isSearching) {
        socket.emit('leave-queue');
      }
    };
  }, [isSearching]);

  const handleFindMatch = () => {
    try {
      setIsSearching(true);
      // Make sure socket is authenticated and connected
      socket.auth = { token };
      socket.connect();

      socket.on('connect', () => {
        console.log('Emitting join-queue');
        socket.emit('join-queue');
      });
    } catch (error) {
      console.error('Error finding match:', error);
      setIsSearching(false);
    }
  };

  const handleCancelSearch = () => {
    setIsSearching(false);
    socket.emit('leave-queue');
    console.log('Leaving queue...');
  };

  const handleGuess = (guessedAI: boolean) => {
    if (!gameSession) return;

    socket.emit('make-guess', {
      sessionId: gameSession.sessionId,
      guessedAI
    });
  };

  if (gameSession) {
    return (
      <div className="container mx-auto px-4 py-8">
        <ChatWindow
          sessionId={gameSession.sessionId}
          opponent={gameSession.opponent}
          onGuess={handleGuess}
        />
      </div>
    );
  }

  return (
    <div className="flex flex-col items-center justify-center min-h-[80vh]">
      {!isSearching ? (
        <button
          onClick={handleFindMatch}
          className="px-8 py-4 bg-game-primary text-white rounded-lg hover:bg-game-primary-dark transition-colors"
        >
          Find Match
        </button>
      ) : (
        <div className="text-center">
          <div className="mb-4">
            <div className="text-xl mb-2">Finding your opponent...</div>
            <div className="text-sm text-game-light-400">
              This might take a moment
            </div>
          </div>
          <button
            onClick={handleCancelSearch}
            className="px-6 py-3 bg-red-500 text-white rounded-lg hover:bg-red-600 transition-colors"
          >
            Cancel
          </button>
        </div>
      )}
    </div>
  );
};

export default Game;
